import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function ProtectedRoute({ children }) {
  const [auth, setAuth] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const isAuthenticated = localStorage.getItem('isAuthenticated') === 'true';
    const userUID = localStorage.getItem('userUID');
    const userEmail = localStorage.getItem('userEmail');

    // Redirect to login if not authenticated or if UID/Email are missing
    if (!isAuthenticated || !userUID || !userEmail) {
      navigate('/login'); // Redirect to login
    } else {
      setAuth(true); // Set authenticated state to true
    }
  }, [navigate]);

  return auth ? children : null; // Render children (protected page) only if authenticated
}

export default ProtectedRoute;
