import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Home() {
  const navigate = useNavigate();

  useEffect(() => {
    // Capture the initial state of the localStorage upon page load
    const isAuthenticated = localStorage.getItem('isAuthenticated');
    const userUID = localStorage.getItem('userUID');
    const userEmail = localStorage.getItem('userEmail');
    const userDisplayName = localStorage.getItem('userDisplayName');

    // Check if any required user data is null or missing
    if (
      isAuthenticated === 'true' && 
      (userUID === null || userEmail === null || userDisplayName === null)  // Explicitly check for NULL values
    ) {
      // Reset authentication state
      localStorage.setItem('isAuthenticated', 'false');

      // Alert the user about tampering
      alert("Warning: Don't tamper with the authentication! Please log in properly.");

      // Redirect to login page
      navigate('/login');
    }
  }, [navigate]);

  const userDisplayName = localStorage.getItem('userDisplayName'); // Get displayName

  const handleLogout = () => {
    fetch('/api/logout', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
    })
    .then((response) => {
      if (response.ok) {
        // Set authentication state to false in localStorage or cookies
        localStorage.setItem('isAuthenticated', 'false');

        // Clear user information from localStorage
        localStorage.removeItem('userUID');
        localStorage.removeItem('userEmail');
        localStorage.removeItem('userDisplayName');

        // Redirect to login page
        navigate('/login');
      } else {
        throw new Error('Logout failed');
      }
    })
    .catch((error) => console.error('Error logging out:', error));
  };

  return (
    <div className="container">
      <h2>Welcome to the Home Page</h2>
      {userDisplayName && <h2>Hello, {userDisplayName}!</h2>}  {/* Display displayName */}
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
}

export default Home;
